  public function multid_sort($arr, $index) {

      $b = array();

      $c = array();

      foreach ($arr as $key => $value) {

        $b[$key] = $value[$index];

      }

      arsort($b);

      foreach ($b as $key => $value) {

        $c[] = $arr[$key];

      }

      return $c;

    }

